import './App.css';
import { useState, useEffect } from 'react';
import "leaflet/dist/leaflet.css";
import Table from 'react-bootstrap/Table';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import axios from 'axios';
import CasesByCountry from './components/CasesByCountry';
import HeaderCards from './components/HeaderCards';
import FormControl from './components/FormControl';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles/style.css';
import MapLeaflet from './components/MapLeaflet'


const App =() => {
  const[countries,setCountries] = useState([]);
  const [isLoadingTable, setIsLoadingTable] = useState(true);
  const[cardData,setCardData] = useState([]);
  const[isLoadingCard, setIsLoadingCard] = useState(true);
  const [mapCenter, setMapCenter] = useState({ lat: 37.80746, lng: -80.4796});

  /*const [getCardInfos, setGetCardInfos] = useState("https://disease.sh/v3/covid-19/all?yesterday=true&twoDaysAgo=true&allowNull=true");
  const [selectedCountry, setSelectedCountry] = useState("WorldWide");*/
  const onChangeCountry = async (e) => {
    const countryCode = e.target.value;
    const urlCards = (countryCode === "WorldWide" ? `https://disease.sh/v3/covid-19/all?yesterday=true&twoDaysAgo=true&allowNull=true` :
      `https://disease.sh/v3/covid-19/countries/${countryCode}?yesterday=true&twoDaysAgo=true&strict=true&allowNull=false`)
    const resultOption = await axios(urlCards);
    setCardData(resultOption.data);
    //setMapCenter([cardData.countryInfo.lat, cardData.countryInfo.long]);
    setIsLoadingCard(false);
    console.log(cardData.countryInfo);
   /* console.log(e.target.value);
    await setSelectedCountry(e.target.value).then(() => {
      console.log(selectedCountry);
      setGetCardInfos(selectedCountry === "WorldWide" ? `https://disease.sh/v3/covid-19/all?yesterday=true&twoDaysAgo=true&allowNull=true` :
        `https://disease.sh/v3/covid-19/countries/${selectedCountry}?yesterday=true&twoDaysAgo=true&strict=true&allowNull=true`)
    }).then(async () => {
      console.log(getCardInfos);
      const resultOption = await axios(getCardInfos)
      setCardData(resultOption.data)
      setIsLoadingCard(false);
      console.log(cardData);
    })
    */
  }

  useEffect(async () => {
    const result = await axios(`https://disease.sh/v3/covid-19/countries?yesterday=true&twoDaysAgo=true&sort=active&allowNull=true`);
    setCountries(result.data);
    setIsLoadingTable(false);
    const resultOption = await axios(`https://disease.sh/v3/covid-19/all?yesterday=true&twoDaysAgo=true&allowNull=true`);
    setCardData(resultOption.data);
    setIsLoadingCard(false);
    console.log(cardData);
  }, []);
  return (
    <>
        <Container fluid={true}>
          <Row className="my-4">
            <Col xs={12} md={6}>
              <Row>
                <Col xs={12} sm={8}>
                  <h1 className="text-center">COVID-19 TRACKER</h1>
                </Col>
                <Col xs={12} sm={4}>
                  <FormControl countries={countries} onChangeCountry={onChangeCountry}/>
                </Col>
                <Col xs={12}>
                  <HeaderCards cardData={cardData} isLoadingCard={isLoadingCard}/>
                </Col>
              </Row>
              <MapLeaflet mapCenter = {mapCenter}/>
            </Col>
            <Col xs={12} md={6}>
            <div className="tableDiv">
              <Table className="tableComponent">
                <CasesByCountry countries={countries} isLoadingTable={isLoadingTable}/>
              </Table>
            </div>
          </Col>
        </Row>
      </Container>
    </>
  );
}
export default App;
