import numeral from "numeral";
const CasesByCountry = ({ countries }) => {
    class filteredDatas {
        constructor(key, caseTest, deathPop, deathRecv, todayCases, totalCases) {
            this.key = key;
            this.caseTest = caseTest;
            this.daeathPop = deathPop;
            this.deathRecv = deathRecv,
            this.todayCases = todayCases,
            this.totalCases = totalCases;
        }
    }
    const dataArray = [];
    return (
        <>
            <thead className="titleTable">
                <tr>
                    <th className="bg-dark text-light">#</th>
                    <th className="bg-dark text-light">Country</th>
                    <th className="bg-dark text-light">Active Cases</th>
                    <th className="bg-dark text-light">Today Cases</th>
                    <th className="bg-dark text-light">Case/Test</th>
                    <th className="bg-dark text-light">Pop./Death</th>
                    <th className="bg-dark text-light">Death/Recv.</th>
                </tr>
            </thead>
                <tbody>
                    {countries.map((countryData, key) => {
                        filteredDatas.key = key;
                        filteredDatas.caseTest = parseFloat(countryData.cases / countryData.tests).toFixed(2);
                        filteredDatas.deathPop = numeral(parseInt(countryData.population / countryData.deaths)).format('0,0');
                        filteredDatas.deathRecv = numeral(countryData.todayCases).format('0,0');
                        filteredDatas.todayCases = numeral(countryData.todayCases).format('0,0');
                        filteredDatas.totalCases = numeral(countryData.active).format('0,0');
                        dataArray.push(filteredDatas);
                        console.log(dataArray);
                        /*
                        let caseTest = parseFloat(countryData.cases / countryData.tests).toFixed(2);
                        let deathPop = numeral(parseInt(countryData.population / countryData.deaths)).format('0,0');
                        let deathRecv = parseFloat(countryData.deaths / countryData.recovered).toFixed(2);
                        let todayCases = numeral(countryData.todayCases).format('0,0');
                        let totalCases = numeral(countryData.active).format('0,0');
                        if (caseTest == Infinity) {
                            caseTest = "NaN";
                        }
                        if (deathRecv == Infinity) {
                            deathRecv = "NaN";
                        }
                        return (
                            <tr key={key}>
                                <td className="text-right">{key + 1}</td>
                                <td className="pl-2"><strong>{countryData.country}</strong></td>
                                <td className="pl-2 text-right">{totalCases}</td>
                                <td className="pl-2 text-right">{todayCases}</td>
                                <td className="pl-2 text-right">{caseTest}</td>
                                <td className="pl-2 text-right">{deathPop}</td>
                                <td className="pl-2 text-right">{deathRecv}</td>
                            </tr>
                        );*/
                        })
                    }
                </tbody>
        </>
    )
}
export default CasesByCountry;
