import numeral from "numeral";
const CasesByCountry = ({ countries }) => {
    return (
        <>
            <thead className="titleTable">
                <tr>
                    <th className="bg-dark text-light">#</th>
                    <th className="bg-dark text-light">Country</th>
                    <th className="bg-dark text-light">Active Cases</th>
                    <th className="bg-dark text-light">Today Cases</th>
                    <th className="bg-dark text-light">Case/Test</th>
                    <th className="bg-dark text-light">Pop./Death</th>
                    <th className="bg-dark text-light">Death/Recv.</th>
                </tr>
            </thead>
                <tbody>

                    {countries.map((countryData, key) => {
                        let caseTest = parseFloat(countryData.cases / countryData.tests).toFixed(2);
                        let deathPop = parseInt(countryData.population / countryData.deaths);
                        let deathRecv = parseFloat(countryData.deaths / countryData.recovered).toFixed(2);
                        let todayCases = numeral(countryData.todayCases).format('0,0');
                        let totalCases = numeral().format();
                        
                        if (caseTest == Infinity) {
                            caseTest = "NaN";
                        }
                        if (deathRecv == Infinity) {
                            deathRecv = "NaN";
                        }
                        return (
                            <tr key={key}>
                                <td>{key + 1}</td>
                                <td className="pl-2 tex-right"><strong>{countryData.country}</strong></td>
                                <td className="pl-2 text-right">{countryData.cases}</td>
                                <td className="pl-2 text-right">{todayCases}</td>
                                <td className="pl-2 text-right">{caseTest}</td>
                                <td className="pl-2 text-right">{deathPop}</td>
                                <td className="pl-2 text-right">{deathRecv}</td>
                            </tr>
                        );
                    })}
                </tbody>
            
            
        </>
    )
}
export default CasesByCountry;
