import React from 'react';
import { MapContainer , TileLayer, useMap, Marker, Tooltip } from 'react-leaflet';
import "leaflet/dist/leaflet.css";
import L from 'leaflet';

const MapLeaflet = ({mapCenter, mapZoom}) => {
    const iconMarker = L.Icon.extend({
        options: {
            iconUrl: require('./placeholder.png'),
            iconRetinaUrl: require('./placeholder.png'),
            iconAnchor: null,
            popupAnchor: null,
            shadowUrl: null,
            shadowSize: null,
            shadowAnchor: null,
            iconSize: new L.Point(60, 75),
            className: 'leaflet-div-icon'
        }
    });
    function ChangeView({ center, zoom }) {
        const map = useMap();
        map.setView(center, zoom);
        return null;
    }
    return (
        <div className="map-All">
            <MapContainer center={mapCenter} zoom={mapZoom} scrollWheelZoom={true}>
                <ChangeView center={mapCenter} zoom={mapZoom} />
                <TileLayer attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors' url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"/>
                <Marker icon position ={[37.80746, -80.4796]} onMouseOver ={(e) => { e.target.openPopup();}} onMouseOut ={(e) => { e.target.closePopup();}}>
                    <Tooltip>Sydney ilkay da ilkay yav </Tooltip>
                </Marker>
            </MapContainer>
        </div>
    )
}
 export default MapLeaflet;
