const CasesByCountry = ({ countries }) => {
    return (
        <>
            <thead>
                <tr className="bg-dark text-light">
                    <th>#</th>
                    <th>Country</th>
                    <th>Active Cases</th>
                    <th>Today Cases</th>
                    <th >Case/Test</th>
                    <th>Pop./Death</th>
                    <th>Death/Recv.</th>
                </tr>
            </thead>
            <tbody>
                <div className="tableBody">
                    {countries.map((countryData, key) => {
                        let caseTest = parseFloat(countryData.cases / countryData.tests).toFixed(2);
                        let deathPop = parseInt(countryData.population / countryData.deaths);
                        let deathRecv = parseFloat(countryData.deaths / countryData.recovered).toFixed(2);
                        if (caseTest == Infinity) {
                            caseTest = "NaN";
                        }
                        if (deathRecv == Infinity) {
                            deathRecv = "NaN";
                        }
                        return (
                            <tr key={key}>
                                <td>{key + 1}</td>
                                <td className="pl-2"><strong>{countryData.country}</strong></td>
                                <td className="pl-2">{countryData.cases}</td>
                                <td className="pl-2">{countryData.todayCases}</td>
                                <td className="pl-2">{caseTest}</td>
                                <td className="pl-2">{deathPop}</td>
                                <td className="pl-2">{deathRecv}</td>
                            </tr>
                        );
                    })}
                </div>
            </tbody>
            
        </>
    )
}
export default CasesByCountry;
