import './App.css';
import { useState, useEffect } from 'react';
import "leaflet/dist/leaflet.css";
import Table from 'react-bootstrap/Table';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles/style.css';
import axios from 'axios';
import CasesByCountry from './components/CasesByCountry';

const App =() => {
  const[countries,setCountries] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  useEffect(async () => {
    const result = await axios(`https://disease.sh/v3/covid-19/countries?yesterday=true&twoDaysAgo=false&sort=cases&allowNull=true`);
    setCountries(result.data);
    setIsLoading(false);
    console.log(countries);
  }, []);
  return (
    <>
        <Container fluid={true}>
          <Row className="my-4">
            <Col xs={12} md={6}><h1 className="text-center">COVID-19 TRACKER</h1></Col>
            <Col xs={12} md={6}>
            <div className="tableBody topOfTable">
              <Table>
                <CasesByCountry countries={countries} isLoading={isLoading} />
              </Table>
            </div>
            </Col>
          </Row>
        </Container>
    </>
  );
}
export default App;
