import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import numeral from "numeral";

const HeaderCards = ({cardData}) => {
    //const cardArray = ["Active Cases", "Total Deaths", "Total Recovered", "Today Cases", "Today Deaths", "Today Recovered"];
    const cardInfos = {
        cardContents:[
            {
                title: "Active Cases",
                dataNumber: cardData.active
            },
            {
                title: "Total Deaths",
                dataNumber: cardData.deaths
            },
            {
                title: "Total Recovered",
                dataNumber: cardData.recovered
            },
            {
                title: "Today Cases",
                dataNumber: cardData.todayCases

            },
            {
                title: "Today Deaths",
                dataNumber: cardData.todayDeaths
            },
            {
                title: "Today Recovered",
                dataNumber: cardData.recovered
            }
        ]
    }
    return (
        <>
        <Row>
                {
                    <>
                <Col xs={6} md={4}>
                    <Card>
                        <h2>{}</h2>
                        <h3>{cardData.active}</h3>
                    </Card>
                </Col>
                </>}
        </Row>
        </>
    )
}
export default HeaderCards;
