const CasesByCountry = ({ countries }) => {
    return (
        <>
            <thead className="topOfTable">
                <tr className="bg-dark text-light text-center">
                    <th>#</th>
                    <th>Country</th>
                    <th>Active Cases</th>
                    <th>Today Cases</th>
                    <th >Case/Test</th>
                    <th>Pop./Death</th>
                    <th>Death/Recv.</th>
                </tr>
            </thead>
            {countries.map((countryData, key) => {
                let caseTest = parseFloat(countryData.cases/countryData.tests).toFixed(2);
                let deathPop = parseInt(countryData.population/countryData.deaths);
                let deathRecv = parseFloat(countryData.deaths/countryData.recovered).toFixed(2);
                if(caseTest == Infinity){
                    caseTest = "Nan"
                }
                return(
                    <tbody key={key}>
                        <tr className="text-center">
                            <td>{key}</td>
                            <td><strong>{countryData.country}</strong></td>
                            <td>{countryData.cases}</td>
                            <td>{countryData.todayCases}</td>
                            <td>{caseTest}</td>
                            <td>{deathPop}</td>
                            <td>{deathRecv}</td>
                        </tr>
                    </tbody>
                );
            })}
        </>
    )
}
export default CasesByCountry;
