import './App.css';
import { useState, useEffect } from 'react';
import "leaflet/dist/leaflet.css";
import Table from 'react-bootstrap/Table';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import axios from 'axios';
import CasesByCountry from './components/CasesByCountry';
import HeaderCards from './components/HeaderCards';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles/style.css';

const App =() => {
  const[countries,setCountries] = useState([]);
  const [isLoadingTable, setIsLoadingTable] = useState(true);
  const[cardData,setCardData] = useState([]);
  const[isLoadingCard, setIsLoadingCard] = useState(true);
  useEffect(async () => {
    const result = await axios(`https://disease.sh/v3/covid-19/countries?yesterday=true&twoDaysAgo=true&sort=active&allowNull=true`);
    setCountries(result.data);
    setIsLoadingTable(false);  
  }, []);
  useEffect(async () => {
    const result = await axios(`https://disease.sh/v3/covid-19/all?yesterday=true&twoDaysAgo=true&allowNull=true`);
    setCardData(result.data);
    setIsLoadingCard(false);
    console.log(cardData);
  },[]);
  return (
    <>
        <Container fluid={true}>
          <Row className="my-4">
            <Col xs={12} md={6}>
              <Row>
                <Col xs={12}></Col>
              <h1 className="text-center">COVID-19 TRACKER</h1>
              <HeaderCards cardData={cardData} isLoadingCard={isLoadingCard}/>
              </Row>
            </Col>
            <Col xs={12} md={6}>
            <div className="tableDiv">
              <Table className="tableComponent">
                <CasesByCountry countries={countries} isLoadingTable={isLoadingTable}/>
              </Table>
            </div>
          </Col>
        </Row>
      </Container>
    </>
  );
}
export default App;
